-- Records of t_exception_log
-- ----------------------------
INSERT INTO `t_exception_log` VALUES ('1', 'system', '2130706433', 'com.pan.controller.ArticleController', 'toArticleDetailPage', 'ArithmeticException', '/ by zero', '1', '2019-04-16 17:11:18');
